import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class FavoriteService {
    constructor(http) {
        this.http = http;
        //adding a favorite cocktail
        this.addFavUrl = "http://localhost:8090/addfavorite";
        //viewing user favorite cocktails
        this.viewFavsUrl = "http://localhost:8090/viewfavorites";
    }
    async addFavorite(user_id, drink) {
        var fav = {
            user_id: user_id,
            drink: drink
        };
        let response = await fetch(this.addFavUrl, {
            method: "POST",
            mode: 'cors',
            body: JSON.stringify(fav),
            credentials: "include"
        });
        if (response.status === 201) {
            console.log("Favorite drink added.");
        }
    }
    async viewFavorites(user_id) {
        let favsArray;
        let response = await fetch(this.viewFavsUrl, {
            method: "POST",
            mode: 'cors',
            body: JSON.stringify(user_id),
            credentials: "include"
        });
        if (response.status === 200) {
            favsArray = response.body;
            console.log("Favorite drink added.");
        }
        return favsArray;
    }
}
